import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user.data || {});
  const userToken = useSelector(store => store.user.token.idToken.jwtToken || {});
  const { user = {} } = userStore;

  const history = useHistory();

  console.log(userToken)
  console.log((user.cpf === undefined))

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (!!userToken) history.push("/");
        break;
      case "not-exists":
        if (!userToken) history.push("/login");
        break;
      case "incomplete-registration":
        // if (!userStore.token) history.push("/login");
        if ((userToken !== {}) && (user.cpf === undefined))  history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user, userToken]);

  return <></>;
};

export default AuthGuard;
