import React, {useState ,useEffect} from "react";
import { API } from "aws-amplify";
import config from "../../config/constants";
import destaque from "../../assets/img/view-of-a-concert.png";
import desktop from "../../assets/img/somlivre-desktop.png";
import smartphone from "../../assets/img/somlivre-smartphone.png";
import tablet from "../../assets/img/somlivre-tablet.png";

import "./AboutUs.scss";

export default function (props) {

  const [aboutUsTitle, setAboutUsTitle] = useState({});
  const [aboutUsSubtitle, setAboutUsSubtitle] = useState({});

  useEffect(() => {
    API.get(config.APIS.SOMLIVRE_PUBLIC, `site-info`, {})
       .then(response => {
          setAboutUsTitle(response.data.about_us_title)
          setAboutUsSubtitle(response.data.about_us_subtitle)
       })
       .catch(error => {
          console.log("get user data falhou: ", error);
       });
  },[]);

  return ( 
    <>
      <section className="container-aboutUs">
        <picture className="imgDestaque">
          <img src={destaque} alt="Imagem de Show" />
        </picture>

        <div className="container-content">
          <h2>{aboutUsTitle ? `${aboutUsTitle}` : "Sua plataforma de shows online ao vivo"}</h2>
          <p>{aboutUsSubtitle ? `${aboutUsSubtitle}` : 
            `A plataforma de shows por streaming da Som Livre é o lugar perfeito 
              para você assistir aos shows exclusivos dos seus ídolos, de onde
              estiver, com vídeo e áudio de qualidade.`
            }
          </p>

          <div className="content">
            <div className="example">
              <img className="desktop" src={desktop} alt="Exemplo do show no desktop" />
              <img className="tablet" src={tablet} alt="Exemplo do show no tablet" />
              <img className="smartphone" src={smartphone} alt="Exemplo do show no smartphone" />
            </div>
            <div className="description">
              <h3>
                Donec malesuada, dui eu condimentum eleifend, magna magna
                ultricies lectus.
              </h3>
              <h4>Integer ac odio mattis.</h4>
              <div>
                Proin ut nisi sit amet eros eleifend commodo.
                <p>
                  Aliquam luctus libero vitae felis ornare, ut aliquet leo
                  tristique. Vestibulum sodales semper efficitur, neque urna
                  tempor nisi, sit amet laoreet ante leo ut mauris.
                </p>
                <p>
                  Nam tincidunt nec magna ut vestibulum. Vestibulum maximus
                  maximus ultricies. Morbi sit amet augue quis nisl egestas
                  semper.
                </p>
                <p>
                  Convallis hendrerit urna, et commodo ante mattis a. Aliquam
                  condimentum, tortor vitae posuere pellentesque, risus nisl
                  ullamcorper augue, in rutrum nibh turpis eu urna.
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

