import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user.data || {});
  const hasRegisterCompleted = user => !!userStore.full_name && !!userStore.cpf && !!userStore.phone_number;
  const userAuth = useSelector(store => store.user.isAuthenticated);
  const history = useHistory();

 console.log(user)

  // console.log(hasRegisterCompleted(user))

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userAuth) history.push("/");
        break;
      case "not-exists":
        if (userAuth) history.push("/login");
        break;
      case "incomplete-registration":
        if (hasRegisterCompleted(user) && userAuth) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user,userAuth]);

  return <></>;
};

export default AuthGuard;
