import React, {useEffect, useState} from "react";
import { Auth, API } from "aws-amplify";
import config from "../../../config/constants";
import { useDispatch, useSelector } from "react-redux";
import { getSiteInfo } from "../../../store/actions/siteinfo.actions";
import { setUserData } from "../../../store/actions/user.action";
import Loading from "../../elements/Loading/Loading";
import Header from '../../features/Header/Header';
import Footer from '../../features/Footer/Footer';
import AuthGuard from "./Auth";

const PageContainer = props => {
  const loading = useSelector(store => store.siteInfo.loading);
  const siteInfo = useSelector(store => store.siteInfo.data);
  const dispatch = useDispatch();
  const { auth } = props;

  useEffect(() => {
    if(!siteInfo){
      dispatch(getSiteInfo());
    }
    // eslint-disable-next-line
  }, []);


  const get_authenticated_user = () => {
    const [isAuthenticated, setIsAuthenticated] = useState({})
  Auth.currentAuthenticatedUser()
      .then(response => {
        console.log("get_authenticated_user, response:", response);
        API.get(config.APIS.SOMLIVRE, `usuario/${response.attributes.sub}`, {})
        .then(response => {
            console.log("get user data bem sucedido. Response: ", response);
            setIsAuthenticated(response.data)
        })
        .catch(error => {
          console.log("get user data falhou: ", error);
        });
      })
      .catch(error => {
        console.log(error);
      });
      dispatch(setUserData(isAuthenticated));
  }

  useEffect(() => {
    get_authenticated_user()
  // eslint-disable-next-line
  },[])

  return (
    <>
    <AuthGuard type={auth}/>
      <Header/>
        {loading ? (
          <div className="loadingContainer">
            <Loading />
          </div>
        ):(
          <>{props.children}</>
        )}
      <Footer {...siteInfo}/>
    </>
  )
}

export default PageContainer;
