import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user || {});
  const { data = {} } = userStore;
  const hasRegisterCompleted = data =>
    !!data.full_name && !!data.cpf && !!data.phone_number;
  const history = useHistory();

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userStore.token) history.push("/");
        break;
      case "not-exists":
        if (!userStore.token) history.push("/login");
        break;
      case "incomplete-registration":
        if (!hasRegisterCompleted(data) && userStore.token) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, data]);

  return <></>;
};

export default AuthGuard;
