import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(state => state.user.data || {});
  const history = useHistory();
  const { isAuth } = props

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (isAuth.signInUserSession) history.push("/");
        break;
      case "not-exists":
        if (!isAuth.signInUserSession) history.push("/login");
        break;
      case "incomplete-registration":
        if ((userStore.cpf === undefined) && (isAuth.signInUserSession !== undefined)) history.push("/seusdados"); 
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, isAuth]);

  return <></>;
};

export default AuthGuard;
