const requestUser = () => (
    {
        type: 'REQUEST_USER'
    }
)

const successUser = (payload) => (
    {
        type: 'SUCCESS_USER',
        payload: payload
    }
)

const errorUser = (payload) => (
    {
        type: 'ERROR_USER',
        payload: payload
    }
)

const setUser = (payload) => (
    {
        type: "SET_USER",
        payload: payload
    }
)

const removeUser = () => (
    {
        type: "REMOVE_USER"
    }
)

const successToken = (payload) => (
    {
        type: 'SUCCESS_TOKEN',
        payload: payload
    }
)

const errorToken = (payload) => (
    {
        type: 'ERROR_TOKEN',
        payload: payload
    }
)

const setToken = (payload) => (
    {
        type: "SET_TOKEN",
        payload: payload
    }
)

const removeToken = () => (
    {
        type: "REMOVE_TOKEN"
    }
) 

export const getUser = () => (dispatch) => {
    dispatch(requestUser())
    dispatch(successUser("success"))
    dispatch(errorUser("ERRO"))
}

export const setUserData = (user) => (dispatch) => {
    dispatch(setUser(user))
}

export const removeUserData = () => (dispatch) => {
    dispatch(removeUser())
}

export const getToken = () => (dispatch) => {
    dispatch(requestToken())
    dispatch(successToken("success"))
    dispatch(errorToken("ERRO"))
}

export const setIsToken = (user) => (dispatch) => {
    dispatch(setToken(user))
}

export const removeIsToken = () => (dispatch) => {
    dispatch(removeToken())
}
