import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user.data || {});
  const { user = {} } = userStore;
  const hasRegisterCompleted = user => !!user.name && !!user.cpf;
  const history = useHistory();

  const { isAuth } = props

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userStore.entity_id) history.push("/");
        break;
      case "not-exists":
        if (!userStore.entity_id) history.push("/login");
        break;
      case "incomplete-registration":
        if (!userStore.entity_id) history.push("/login");
        if (!hasRegisterCompleted(user) && isAuth) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user, isAuth]);

  return <></>;
};

export default AuthGuard;
