import React, {useState} from "react";
import Botao from "../../../elements/Botao/Botao";
import moment from 'moment';
import './chamada.scss';
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const Chamada = props => {
   let havePrice = props.havePrice;
   let classFaixaEtaria = "faixa-etaria";
   if(havePrice) {
      classFaixaEtaria += ' mobile';
   }

   const current_event = props.currentEvent;
   const user = useSelector(state => state.user.data);

   const [time, setTIme] = useState({})
   const duracao = () => {
      setInterval(function() {
         const countDownDate = moment(event.date).unix() * 1000
         const now = new Date().getTime();
         const distance = countDownDate - now;

         setTIme({
         days : Math.floor(distance / (1000 * 60 * 60 * 24)),
         hours : Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
         minutes : Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
         seconds : Math.floor((distance % (1000 * 60)) / 1000)
         })

         if (distance < 0) {
         clearInterval(duracao);
         }
      }, 1000);
   }

   const date = value => {
      return moment(value).format('DD/MM - HH:mm');
   };

   return (
      <section className="boxChamada">
            <div className="content">
            { current_event &&
              <>
                <h3>{current_event.name}</h3>
                <p className="aoVivo">ao vivo</p>
                <p className="dataHora">{date(current_event.date)}</p>
                <p className={classFaixaEtaria}>{`Classificação etária: ${current_event.age_rating}`}</p>
                {user.entity_id ? 
                  <time onLoad={duracao()}>
                     <div className="tempo">Dias<p>{time.days}</p></div>
                     <div className="tempo">Horas<p>{`${time.hours}`}</p></div>
                     <div className="ponto">:</div>
                     <div className="tempo">Minutos<p>{`${time.minutes}`}</p></div>
                     <div className="ponto">:</div>
                     <div className="tempo">Segundos<p>{time.seconds}</p></div>
                  </time> 
                : <Botao classes="red big"><Link to="/cadastro">Cadastre-se</Link></Botao>}
                <Link to="/eventos" className="saibaMais">Saiba Mais</Link>
              </>
             }
            </div>
      </section>
   );
};

export default Chamada;
