import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Auth } from "aws-amplify";
import {useSelector} from "react-redux";
import { SimpleInput } from "../../components/elements/Input";
import Botao from "../../components/elements/Botao";

import PageContainer from "../../components/features/PageContainer/PageContainer";
import iconFacebook from "../../assets/img/facebook.svg";
import iconGoogle from "../../assets/img/google_plus.svg";

import "./Login.scss";

function Login(props) {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const user = useSelector(state => state.user.data)

  const handleSubmit = (ev) => {
    ev.preventDefault();
    Auth.signIn(email, senha)
      .then((response) => {
        console.log("Login com sucesso. Response: ", response);
        console.log("Props: ", props);
        props.history.push("/");
      })
      .catch((error) => {
        console.log("Erro fazendo login: ", error);
        if (error.code === "UserNotConfirmedException") {
          props.history.push("/codconfirmacao", { email: email });
        } else {
          alert(error.message);
        }
      });
  };

  return (
    <PageContainer auth={user ? "exists" : "not-exists"}>
      <section className="container-login">
        <div className="login">
          <h2 className="title">Acesse sua conta</h2>
          <form className="form" onSubmit={handleSubmit}>
            <SimpleInput
              id="email"
              type="email"
              name="email"
              placeholder="Ex: seunome@email.com"
              label="E-mail"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required={true}
            />
            <SimpleInput
              id="senha"
              type="password"
              name="senha"
              placeholder="Sua Senha"
              label="Senha"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              required={true}
            />
            <Botao className="botao" classes="red big">
              Entrar
            </Botao>
          </form>

          <div className="social-medias-icons">
              <img
                className="facebook-icon"
                src={iconFacebook}
                alt="Icone Facebook"
              ></img>
              <img
                className="google-icon"
                src={iconGoogle}
                alt="Icone Google"
              ></img>
          </div>

          <div className="forgot-password">
            <Link to="/esquecisenha">Esqueci minha senha</Link>
          </div>

          <div className="no-account">
            <p>Ainda não tem uma conta?</p>
            <Link to="/cadastro">Cadastre-se</Link>
          </div>
        </div>
      </section>
    </PageContainer>
  );
}

export default Login;
