import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {

  const history = useHistory();

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (props.userLog) history.push("/");
        break;
      case "not-exists":
        if (!props.userLog) history.push("/login");
        break;
      case "incomplete-registration":
        // if (!props.userLog) history.push("/login");
        if (props.userLog) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [history, props.type, props.userLog]);

  return <></>;
};

export default AuthGuard;
