import React from "react";
import Botao from "../../../elements/Botao/Botao";
import moment from 'moment';
import './chamada.scss';
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const Chamada = props => {
   let havePrice = props.havePrice;
   let classFaixaEtaria = "faixa-etaria";
   if(havePrice) {
      classFaixaEtaria += ' mobile';
   }

   const current_event = props.currentEvent;
   const user = useSelector(state => state.user.data);

   const date = value => {
      return moment(value).format('DD/MM - HH:mm');
   };

   const evento = moment(event.date).unix() * 1000
   const atual = new Date().getTime()
   const duracao = moment.duration(evento - atual, 'milliseconds');


   return (
      <section className="boxChamada">
            <div className="content">
            { current_event &&
              <>
                <h3>{current_event.name}</h3>
                <p className="aoVivo">ao vivo</p>
                <p className="dataHora">{date(current_event.date)}</p>
                <p className={classFaixaEtaria}>{`Classificação etária: ${current_event.age_rating}`}</p>
                {user.entity_id ? <div>Dias<p>{duracao.days()}</p></div> : <Botao classes="red big"><Link to="/cadastro">Cadastre-se</Link></Botao>}
                <Link to="/quemsomos" className="saibaMais">Saiba Mais</Link>
              </>
             }
            </div>
      </section>
   );
};

export default Chamada;
