import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user.data || {});
  const { user = {} } = userStore;
  const hasRegisterCompleted = user => !!user.entity_id && !!user.cpf;
  const history = useHistory();


  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (!!userStore.entity_id) history.push("/");
        break;
      case "not-exists":
        if (!userStore.entity_id) history.push("/login");
        break;
      case "incomplete-registration":
        if (!userStore.token) history.push("/login");
        if (!hasRegisterCompleted(user)) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user]);

  return <></>;
};

export default AuthGuard;
