import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user || {});
  const { user = {} } = userStore;
  const hasRegisterCompleted = user =>
    !!user.data.full_name && !!user.data.cpf && !!user.data.phone_number;
  const history = useHistory();

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userStore.token) history.push("/");
        break;
      case "not-exists":
        if (!userStore.token) history.push("/login");
        break;
      case "incomplete-registration":
        if (!hasRegisterCompleted(user.data) && userStore.token) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user]);

  return <></>;
};

export default AuthGuard;
