import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const user = useSelector(store => store.user.data || {});
  const hasRegisterCompleted = user => !!user.full_name && !!user.cpf && !!user.phone_number;
  const userAuth = useSelector(store => store.user.isAuthenticated);
  const history = useHistory();


  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userAuth) history.push("/");
        break;
      case "not-exists":
        if (!userAuth) history.push("/login");
        break;
      case "incomplete-registration":
        if (userAuth && (hasRegisterCompleted(user) === false)) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [history, props.type, user,userAuth]);

  return <></>;
};

export default AuthGuard;
