const INIT_STATE = {
    data: {},
    isAuthenticated: false,
    error: false
}

const user = (state = INIT_STATE, action) => {
    switch (action.type){
        case "REQUEST_USER": 
        return {
            ...state
        };
        case "SUCCESS_USER": 
        return {
            ...state,
            data: action.payload
        };
        case "SET_USER": 
        return {
            ...state,
            data: action.payload
        };
        case "ERROR_USER": 
        return {
            ...state,
            data: action.payload,
            error: true
        };
        case "REMOVE_USER": 
        return {
            ...state,
            data: {}
        };
        default: 
        return state
    }
}




export default user;
