import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user.data || {});
  const { user = {} } = userStore;
  const userAuth = useSelector(store => store.user.isAuthenticated);
  const hasRegisterCompleted = user => !!user.full_name && !!user.cpf && !!user.phone_number;
  const history = useHistory();


  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userAuth) history.push("/");
        break;
      case "not-exists":
        if (userAuth) history.push("/login");
        break;
      case "incomplete-registration":
        if (userAuth) history.push("/login");
        if (!hasRegisterCompleted(user)) history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user,userAuth]);

  return <></>;
};

export default AuthGuard;
