import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const AuthGuard = props => {
  const userStore = useSelector(store => store.user.data || {});
  const userToken = useSelector(store => store.user.token || {});
  const { user = {} } = userStore;
  const hasRegisterCompleted = user =>
    !!user.name && !!user.cpf && !!user.phone_number;
  const history = useHistory();

  useEffect(() => {
    switch (props.type) {
      case "exists":
        if (userToken) history.push("/");
        break;
      case "not-exists":
        if (!userToken) history.push("/login");
        break;
      case "incomplete-registration":
        // if (!userStore.token) history.push("/login");
        if (!hasRegisterCompleted(user) && userToken)  history.push("/seusdados");
        break;
      default:
        return;
    }
  }, [userStore, history, props.type, user]);

  return <></>;
};

export default AuthGuard;
