import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import avatarDefault from "../../../../../assets/img/avatarDefault.png";
import "./dropdown.scss";

const Dropdown = (props) => {

    const { isBlack } = props;
    const color = isBlack ? "black" : "";
    const user = useSelector(state => state.user.data);
    // const [drop, setDrop] = useState("none");
    // const [lineLeft,setLineLeft] = useState({transform: "rotate(45deg)"});
    // const [lineRight,setLineRight] = useState({transform: "rotate(-45deg)"})

    // const dropView = () => {
    //     if(drop === "none") {
    //         return (
    //         setDrop("flex"), 
    //         setLineLeft({transform: "rotate(-45deg)"}),
    //         setLineRight({transform: "rotate(45deg)"})
    //         )
    //     } else if (drop === "flex") {
    //         return (
    //         setDrop("none"), 
    //         setLineLeft({transform: "rotate(45deg)"}),
    //         setLineRight({transform: "rotate(-45deg)"})
    //         )
    //     }
    // }

    return (
        <section className={color}>
            <div className={"nicknameUser"} 
            // onClick={dropView}
            >
                {`Olá, ${user.full_name.split(' ', 1)}`}
                <div className="dropdownIndicatorStyle">
                    <span 
                    // style={lineLeft} 
                    className="arrowLineStyleLeft" />
                    <span
                    //  style={lineRight} 
                    className="arrowLineStyleRight" />
                </div>
            </div>

            <div className="isdrop" 
            // style={{display: drop}}
            >
                <div className="dropUser">
                    <div className="infoUser">
                        <img className="avatarUser" src={avatarDefault} alt="" />
                        <div className="user">
                            <div className="nameUser">{user.full_name}</div>
                            <div className="emailUser">{user.email}</div>
                        </div>
                    </div>              

                    <div className="linksUser">
                        <span className="border" />
                        <Link className="userLinks" to="/perfil">Meu Perfil</Link>
                        <Link to="/logout" className="userLinks">Sair</Link>
                    </div>
                </div>
            </div>            
        </section>
    )
}

export default Dropdown;
