import React, { useEffect, useState } from "react";
import config from "../../../config/constants";
import { API } from "aws-amplify";
import Chamada from './chamada';
import Parceiros from './parceiros';
import './show.scss';

//temp
import destaqueTemp from '../../../assets/img/destaque-home.png';
import destaqueRespTemp from '../../../assets/img/lexa.png';
import { Link } from "react-router-dom";

const Show = props => {

   const [partners, setPartners] = useState([]);
   const [current_event, setCurrent_event] = useState({});
   const [imageDestaq, setImageDestaq] = useState({});

   useEffect(() => {
      API.get(config.APIS.SOMLIVRE_PUBLIC, `site-info`, {})
         .then(response => {
            console.log("get user data bem sucedido. Response: ", response);
            setPartners(response.data.partners)
            setCurrent_event(response.data.current_event)
            setImageDestaq(response.data.cover_home_url)
         })
         .catch(error => {
            console.log("get user data falhou: ", error);
         });
   },[])

   const havePrice = true;
   return (
      <section id="ShowDestaque">
         <picture className="imgDestaque">
            {  imageDestaq ?
               <> 
                  <img src={imageDestaq} alt="Nome do Show" />
               </> :
               <>
                  <source srcSet={destaqueRespTemp} media="(max-width: 600px)" />
                  <img src={destaqueTemp} alt="Nome do Show" />
               </>
            }
         </picture>
         <Chamada havePrice={havePrice} current_event={current_event}/>
         <Link className="details">Confira aqui os detalhes do Show</Link>
         <Parceiros partners={partners} />
      </section>
   );
};

export default Show;
