import React, { useEffect } from "react";
import PageContainer from "../../components/features/PageContainer/PageContainer";
import "./eventDetail.scss";
import { useSelector } from "react-redux";
import moment from 'moment';
import Iframe from 'react-iframe';
import { Link } from "react-router-dom";
import ReactJWPlayer from "react-jw-player"

export default function () {

  const siteInfo = useSelector(store => store.siteInfo.data);
  const event = siteInfo ? siteInfo.current_event : {}
  const user = useSelector(state => state.user.data);

  const [time, setTIme] = useState({})

  const countDownDate = moment(event.date).unix() * 1000

  const duracao = setInterval(function() {
  const now = new Date().getTime();    
  const distance = countDownDate - now;

  setTIme({
  days : Math.floor(distance / (1000 * 60 * 60 * 24)),
  hours : Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
  minutes : Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
  seconds : Math.floor((distance % (1000 * 60)) / 1000)
})

  if (distance < 0) {
    clearInterval(duracao);
  }
}, 1000);

useEffect(() => {
  duracao()
},[])

  // const evento = moment(event.date).unix() * 1000
  // const atual = new Date().getTime()
  // const duracao = moment.duration(evento - atual, 'milliseconds');

  // const date = value => {
  //   return moment(value).format('DD/MM - HH:mm');
  // };

  return (
    <PageContainer>
      <section className="container-evento">
        <div className="destaque">
          {user.full_name ?
            <div className="player">
              <ReactJWPlayer
              image={event.cover_home_url}
              aspectRatio="currently"
              playerId='event-player'
              playerScript='https://cdn.jwplayer.com/libraries/nLJ43GyD.js'
              file='https://streaming.cnnbrasil.com.br/cnndigital_mainOutput2.m3u8'
              />
            </div>
             :
            <div className="imgDestaque" style={{backgroundImage: `url(${event.cover_url})`}}>
              <h2>{event.name}</h2>
            </div>
          }
        </div>
        <div className="content">
          <Link to="/" className="voltar">{"<< Voltar"}</Link>
          <h3 className="artista">{event.name}</h3>
          <p className="classificacao">{`Classificação etária: ${event.age_rating}`}</p>
          <div className="descricao">{event.highlight_text}</div>
          <div className="date">{date(event.date)}</div>
          <h4 className="title">Laoreet ante leo ut mauris!</h4>
          <Iframe
            className="iframe"
            title="Video Como Funciona"
            src="https://www.youtube.com/embed/dTxjqKNzwDs"
            frameBorder="0"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>
        {!user.email ? 
        <>        
          <div className="infoData">O show começará em</div>
          <div className="contador" style={{backgroundImage: `url(${event.cover_url})`}}>
            <time>
              <div>Dias<p>{time.seconds}</p></div>
              {/* <div>Horas<p>{`${duracao.hours()}h`}</p></div>
              <div>Minutos<p>{duracao.minutes()}</p></div>
              <div>Segundos<p>{duracao.seconds()}</p></div> */}
            </time>
          </div>
        </> 
          : <div className="vazio"></div>
        }
      </section>
    </PageContainer>
  );
}
